    override fun onCreateOptionsMenu(menu: Menu?): Boolean {
        val men = menuInflater
        men.inflate(R.menu.opt_menu, menu)
        return super.onCreateOptionsMenu(menu)
    }

    override fun onOptionsItemSelected(item: MenuItem): Boolean {
        Toast.makeText(this, item.title, Toast.LENGTH_SHORT).show()
        return super.onOptionsItemSelected(item)
    }

    override fun onCreateContextMenu(
        menu: ContextMenu?,
        v: View?,
        menuInfo: ContextMenu.ContextMenuInfo?
    ) {
        super.onCreateContextMenu(menu, v, menuInfo)
        menuInflater.inflate(R.menu.context_menu, menu)
        menu!!.setHeaderTitle("Select Actions")
    }

    override fun onContextItemSelected(item: MenuItem): Boolean {
        when(item.itemId){
            R.id.openNewTap -> Toast.makeText(this,item.title,Toast.LENGTH_SHORT).show()
            R.id.copy -> Toast.makeText(this,item.title,Toast.LENGTH_SHORT).show()
            R.id.copyLink -> Toast.makeText(this,item.title,Toast.LENGTH_SHORT).show()
            R.id.send -> Toast.makeText(this,item.title,Toast.LENGTH_SHORT).show()
            R.id.show -> Toast.makeText(this,item.title,Toast.LENGTH_SHORT).show()
        }
        return super.onContextItemSelected(item)
    }
